mapper.select:
	SELECT m_id, m_pw
		FROM tbl_member
WHERE m_id = #{m_id} AND m_pw = #{m_pw}

mapper.insert:
  		INSERT INTO tbl_member(m_id, m_pw, m_nickname,
  								m_birth, m_name, m_gender,m_profile, m_level)
  		VALUES(
		 #{m_id}, #{m_pw}, #{m_nickname}, 
			#{m_birth}, #{m_name}, #{m_gender}, #{m_profile}, #{m_level}
		)
